ALL_LISTS = {
    'concepticon': CONCEPTICON,
    'wold': WOLD,
    'northeuralex': NORTHEURALEX,
    'zompist': ZOMPIST,
}

def get_list(name: str) -> list:
    """Get a word list by name."""
    return ALL_LISTS.get(name.lower(), [])

def list_all() -> dict:
    """Get all available lists with counts."""
    return {name: len(words) for name, words in ALL_LISTS.items()}

def get_combined() -> set:
    """Get all unique concepts across all lists."""
    combined = set()
    for words in ALL_LISTS.values():
        combined.update(w.lower() for w in words)
    return combined

if __name__ == '__main__':
    print("Word Lists Summary:")
    for name, count in list_all().items():
        print(f"  {name}: {count} words")
    print(f"  Combined unique: {len(get_combined())} words")
